import React from "react";
import "./style.css";
import { Helmet, HelmetProvider } from "react-helmet-async";
import { Container, Row, Col } from "react-bootstrap";
import { dataportfolio, meta } from "../../content_option";

export const Portfolio = () => {
  // Group portfolio items by category
  const groupedPortfolio = dataportfolio.reduce((acc, item) => {
    const category = item.category || "Other";
    if (!acc[category]) {
      acc[category] = [];
    }
    acc[category].push(item);
    return acc;
  }, {});

  return (
    <HelmetProvider>
      <Container className="About-header">
        <Helmet>
          <meta charSet="utf-8" />
          <title> Portfolio | {meta.title} </title>{" "}
          <meta name="description" content={meta.description} />
        </Helmet>
        <Row className="mb-5 mt-3 pt-md-3">
          <Col lg="8">
            <h1 className="display-4 mb-4"> Portfolio </h1>{" "}
            <hr className="t_border my-4 ml-0 text-left" />
          </Col>
        </Row>
        {Object.keys(groupedPortfolio).map((category) => (
          <div key={category} className="mb-5">
            <Row className="mb-4">
              <Col lg="12">
                <h2 className="color_sec mb-3">{category}</h2>
                <hr className="t_border my-3 ml-0 text-left" />
              </Col>
            </Row>
            <div className="po_items_ho">
              {groupedPortfolio[category].map((data, i) => {
                return (
                  <div key={i} className={`po_item ${!data.img ? 'no-image' : ''}`}>
                    {data.img && <img src={data.img} alt="" />}
                    <div className="content">
                      {data.title && <h4 className="mb-3">{data.title}</h4>}
                      {data.date && <p className="text-muted mb-2"><small>{data.date}</small></p>}
                      <p>{data.description}</p>
                      {data.link && <a href={data.link}>view project</a>}
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
        ))}
      </Container>
    </HelmetProvider>
  );
};
